import { Component } from 'react';
import {backendPath} from './settings';

class Activate extends Component {
    async componentDidMount() {
        console.log("uid:",this.props.match.params.uidb64);
        try{
            await fetch(backendPath+'/map/'+this.props.match.params.uidb64+'/'+this.props.match.params.token+'/')
            .then(res => {
                if(res.ok) alert("Activation success");
                else alert("Activation fail");
            });

        } catch (e) {
            alert("Invalid link");
            console.log(e);
        }
    }
    render() {        
        return(<div></div>);
    }
}
export default Activate;
