import React, { Component } from 'react';
import { Navigate } from "react-router-dom";
import './user.css';
import {jwtVerify, logout} from './auth';

class User extends Component {
    constructor(props){
        super(props);
        this.state = {
            isMounted: false,
            username: '',
        };
    }
    componentDidMount(){
        this.setState({isMounted: true});
        jwtVerify()
        .then(res => {
            if(this.state.isMounted) this.setState({username: localStorage.getItem('username')});
        });
        return () => {
            this.setState({isMounted: false});
        }        
    }
    logoutOnClick() {
        logout();
        this.setState({username: ''});
    }
    render() {
        console.log("Mounted:",this.state.isMounted);
        console.log("username:",this.state.username);
        if(this.state.username || !this.state.isMounted) {
            return(
                <div className='userInfo'>
                    <span>User: {this.state.username}</span>
                    <button onClick={this.logoutOnClick}>
                        Logout
                    </button>
                </div>
            );
        }
        else{
            return(
                <Navigate to="/login/" replace={true} />
            );
        }
    }
}
export default User;
