import { Component } from 'react';
import { useParams } from 'react-router-dom';
import {backendPath} from './settings';

function Activate(){
    const { uidb64, token } = useParams();
    console.log("uid:",uidb64);
    try{
        fetch(backendPath+'/map/'+uidb64+'/'+token+'/')
        .then(res => {
            if(res.ok) alert("Activation success");
            else alert("Activation fail");
        });

    } catch (e) {
        alert("Invalid link");
        console.log(e);
    }     
    return(<div>Activation page</div>);
}
export default Activate;
