import _axios from "axios"


const axios = (baseURL) => {
    _axios.defaults.headers.common["Authorization"] = `Bearer ${localStorage.getItem('access_jwt')}`;
    return _axios.create({
        baseURL: baseURL || 'http://localhost:8000',
        timeout: 1000,
    })
}
export default axios();
